import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [CommonModule],
  template: `
  <header class="header">
    <div class="container">
      <a href="#" class="logo" aria-label="Home">
        <img src="assets/icon-shield.svg" alt="" width="32" height="32" />
        <span>CEH Program</span>
      </a>

      <!-- Hamburger button -->
      <button class="menu-toggle" (click)="toggleMenu()" aria-label="Toggle navigation">
        <span [class.open]="menuOpen"></span>
        <span [class.open]="menuOpen"></span>
        <span [class.open]="menuOpen"></span>
      </button>

      <!-- Navigation -->
      <nav [class.open]="menuOpen" aria-label="Primary">
        <ul class="nav-list">
          <li><a href="#highlights" (click)="closeMenu()">Highlights</a></li>
          <li><a href="#curriculum" (click)="closeMenu()">Curriculum</a></li>
          <li><a href="#about" (click)="closeMenu()">About</a></li>
          <li><a href="#register" class="btn" aria-label="Register for the program" (click)="closeMenu()">Register</a></li>
        </ul>
      </nav>
    </div>
  </header>
  `,
  styles: [`
    /* Header container */
    .header {
      background: linear-gradient(90deg, #1e3c72, #2a5298);
      color: #fff;
      padding: 1rem 2rem;
      box-shadow: 0 4px 6px rgba(0,0,0,0.1);
      position: sticky;
      top: 0;
      z-index: 100;
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    }

    .container {
      display: flex;
      align-items: center;
      justify-content: space-between;
      max-width: 1200px;
      margin: 0 auto;
    }

    /* Logo */
    .logo {
      display: flex;
      align-items: center;
      font-weight: bold;
      font-size: 1.2rem;
      text-decoration: none;
      color: #fff;
    }

    .logo img {
      margin-right: 0.5rem;
      transition: transform 0.3s ease;
    }

    .logo:hover img {
      transform: rotate(15deg);
    }

    /* Navigation - Desktop (always visible) */
    nav {
      display: flex;
    }

    nav ul {
      display: flex;
      list-style: none;
      gap: 1.5rem;
      margin: 0;
      padding: 0;
      align-items: center;
    }

    nav ul li a {
      text-decoration: none;
      color: #fff;
      font-weight: 500;
      padding: 0.5rem 0.8rem;
      border-radius: 6px;
      transition: background 0.3s, color 0.3s;
    }

    nav ul li a:hover {
      background: rgba(255, 255, 255, 0.2);
      color: #000;
    }

    /* Register button */
    .btn {
      background: #bb2929ff;
      color: #fff;
      font-weight: bold;
      transition: transform 0.2s, box-shadow 0.2s;
    }

    .btn:hover {
      transform: translateY(-2px);
      box-shadow: 0 4px 10px rgba(0,0,0,0.2);
    }

    /* Hamburger button */
    .menu-toggle {
      display: none;
      flex-direction: column;
      justify-content: center;
      gap: 5px;
      width: 30px;
      height: 25px;
      background: none;
      border: none;
      cursor: pointer;
      z-index: 200;
    }

    .menu-toggle span {
      height: 3px;
      background: white;
      border-radius: 2px;
      transition: 0.3s;
    }

    .menu-toggle span.open:nth-child(1) {
      transform: rotate(45deg) translate(5px, 5px);
    }

    .menu-toggle span.open:nth-child(2) {
      opacity: 0;
    }

    .menu-toggle span.open:nth-child(3) {
      transform: rotate(-45deg) translate(5px, -5px);
    }

    /* Mobile / Tablet */
    @media(max-width: 992px) {
      .menu-toggle {
        display: flex;
      }

      nav {
        display: none;   /* hidden by default */
        position: absolute;
        top: 70px;
        right: 0;
        width: 100%;
        background: #1e3c72;
        flex-direction: column;
        text-align: center;
        padding: 0;
      }

      nav.open {
        display: flex;   /* show when toggled */
        padding: 1rem 0;
      }

      nav ul {
        flex-direction: column;
        gap: 1rem;
      }

      .btn {
        width: 90%;
        text-align: center;
      }
    }
  `]
})
export class HeaderComponent {
  menuOpen = false;

  toggleMenu() {
    this.menuOpen = !this.menuOpen;
  }

  closeMenu() {
    this.menuOpen = false;
  }
}
