import { Component, HostListener, ElementRef } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [CommonModule],
  template: `
  <header class="header">
    <div class="container">
      <a href="#" class="logo" aria-label="Home">
        <img src="assets/icon-shield.svg" alt="" width="32" height="32" />
        <span>CEH Program</span>
      </a>

      <!-- Hamburger -->
      <button
        class="menu-toggle"
        (click)="toggleMenu($event)"
        [attr.aria-expanded]="menuOpen"
        aria-controls="primary-nav"
        aria-label="Toggle navigation"
        type="button"
      >
        <span [class.open]="menuOpen"></span>
        <span [class.open]="menuOpen"></span>
        <span [class.open]="menuOpen"></span>
      </button>

      <!-- nav wrapper -->
      <div id="primary-nav" class="nav-wrapper" [class.open]="menuOpen" role="navigation" aria-label="Primary">
        <nav>
          <ul class="nav-list">
            <li><a href="#highlights" (click)="closeMenu()">Highlights</a></li>
            <li><a href="#curriculum" (click)="closeMenu()">Curriculum</a></li>
            <li><a href="#about" (click)="closeMenu()">About</a></li>
            <li><a href="#register" class="btn" (click)="closeMenu()">Register</a></li>
          </ul>
        </nav>
      </div>
    </div>
  </header>
  `,
  styles: [`
    /* ...same CSS as previous working version... */
  `]
})
export class HeaderComponent {
  menuOpen = false;

  constructor(private el: ElementRef) { }

  toggleMenu(event: Event) {
    event.stopPropagation(); // prevent document click from immediately closing menu
    this.menuOpen = !this.menuOpen;
  }

  closeMenu() {
    this.menuOpen = false;
  }

  // Listen for clicks anywhere in the document
  @HostListener('document:click', ['$event'])
  onDocumentClick(event: Event) {
    const clickedInside = this.el.nativeElement.contains(event.target);
    if (!clickedInside && this.menuOpen) {
      this.menuOpen = false;
    }
  }
}
