import { Component } from '@angular/core';
import { NgIf } from '@angular/common';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [NgIf],
  template: `
  <header class="header">
    <div class="container">
      <!-- Logo -->
      <a href="#" class="logo" aria-label="Home">
        <img src="assets/icon-shield.svg" alt="" width="32" height="32" />
        <span>CEH Program</span>
      </a>

      <!-- Search (desktop only) -->
      <div class="search-box">
        <input 
          type="text" 
          placeholder="Search..."
          [(ngModel)]="searchQuery"
          (keyup.enter)="onSearch()" 
          aria-label="Search program content"
        />
        <button (click)="onSearch()" aria-label="Search">
          üîç
        </button>
      </div>

      <!-- Hamburger (mobile only) -->
      <button class="menu-toggle" (click)="toggleMenu()" aria-label="Toggle menu" [attr.aria-expanded]="menuOpen">
        ‚ò∞
      </button>

      <!-- Nav -->
      <nav aria-label="Primary" [class.open]="menuOpen">
        <ul class="nav-list">
          <li><a href="#highlights" (click)="closeMenu()">Highlights</a></li>
          <li><a href="#curriculum" (click)="closeMenu()">Curriculum</a></li>
          <li><a href="#about" (click)="closeMenu()">About</a></li>
          <li><a href="#register" class="btn" aria-label="Register for the program" (click)="closeMenu()">Register</a></li>

          <!-- Search (mobile version, inside nav) -->
          <li class="search-mobile">
            <input 
              type="text" 
              placeholder="Search..."
              <!-- [(ngModel)]="searchQuery" -->
              (keyup.enter)="onSearch()" 
              aria-label="Search program content"
            />
            <button (click)="onSearch()" aria-label="Search">üîç</button>
          </li>
        </ul>
      </nav>
    </div>
  </header>
  `,
  styles: [`
    /* Header styles remain same... */

    /* Search box (desktop) */
    .search-box {
      display: flex;
      align-items: center;
      margin: 0 1rem;
    }

    .search-box input {
      padding: 0.4rem 0.8rem;
      border-radius: 6px 0 0 6px;
      border: none;
      outline: none;
      font-size: 0.9rem;
    }

    .search-box button {
      padding: 0.4rem 0.8rem;
      border: none;
      border-radius: 0 6px 6px 0;
      cursor: pointer;
      background: #fff;
      color: #1e3c72;
      font-size: 1rem;
      transition: background 0.3s;
    }

    .search-box button:hover {
      background: #f0f0f0;
    }

    /* Mobile search inside nav */
    .search-mobile {
      display: none;
    }

    @media(max-width: 768px){
      .search-box {
        display: none; /* hide desktop search */
      }

      .search-mobile {
        display: flex;
        flex-direction: row;
        align-items: center;
        gap: 0.5rem;
      }

      .search-mobile input {
        flex: 1;
        padding: 0.4rem 0.8rem;
        border-radius: 6px;
        border: none;
        outline: none;
      }

      .search-mobile button {
        padding: 0.4rem 0.8rem;
        border: none;
        border-radius: 6px;
        background: #fff;
        color: #1e3c72;
        cursor: pointer;
      }
    }
  `]
})
export class HeaderComponent {
  menuOpen = false;
  searchQuery = '';

  toggleMenu() {
    this.menuOpen = !this.menuOpen;
  }

  closeMenu() {
    this.menuOpen = false;
  }

  onSearch() {
    if (this.searchQuery.trim()) {
      console.log('Searching for:', this.searchQuery);
      // TODO: hook up to real search logic (e.g., filter list or navigate)
    }
  }
}
