import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-hero',
  standalone: true,
  imports: [CommonModule],
  template: `
  <section class="section hero-bg" id="hero">


      <!-- Your same content ... -->
      <div class="container">

        <span class="kicker">EC-Council Certified</span>
        <h1>Cyber Security – <span style="color: var(--accent)">Certified Ethical Hacking (CEH) Program</span></h1>
        <p>Become a job-ready ethical hacker with hands-on labs, real-world attack simulations, and expert mentorship.</p>
        <div class="cta-buttons">
          <a class="btn" href="#register">Register Now</a>
          <a class="btn outline" href="#curriculum">Explore Curriculum</a>
        </div>
        <ul class="features">
          <li class="card"><img src="assets/icon-labs.svg" alt="Labs" width="22" height="22"> 20+ Guided Labs</li>
          <li class="card"><img src="assets/icon-certificate.svg" alt="Certificate" width="22" height="22"> EC-Council Certificate</li>
          <li class="card"><img src="assets/icon-clock.svg" alt="Hours" width="22" height="22"> 80–100 Hours</li>
        </ul>
      </div>
    </div>
  </section>
  `,
  styles: [`
    .hero-slider { position: relative; min-height: 90vh; overflow: hidden; }
    .slide-img {
      position: absolute; inset: 0; width: 100%; height: 100%;
      object-fit: cover; opacity: 0; transition: opacity 1s ease-in-out; will-change: opacity;
    }
    .slide-img.active { opacity: 1; z-index: 0; }
    .overlay { position: absolute; inset: 0; background: rgba(0,0,0,.55); z-index: 1; }
    .container { position: relative; z-index: 2; padding: 2rem; color:#fff; max-width:1000px; }
    /* …keep the rest of your styles/responsive rules… */
  `]
})
export class HeroComponent implements OnInit, OnDestroy {
  images = ['assets/hero1.jpg', 'assets/hero2.jpg', 'assets/hero3.jpg'];
  currentIndex = 0;
  private intervalId?: number;

  ngOnInit() {
    this.intervalId = window.setInterval(() => {
      this.currentIndex = (this.currentIndex + 1) % this.images.length;
    }, 5000);
  }
  ngOnDestroy() { if (this.intervalId) window.clearInterval(this.intervalId); }
}
